mapper.select:
SELECT d.keyId, d.dicType, d.dicTypeCode, d.dicValue, d.delFlag, u.userName AS updateUser, DATE_FORMAT( d.updateTime, '%Y-%m-%d %H:%i:%s' ) AS updateTime FROM dictionary_info d LEFT JOIN user_info u ON d.updateUser = u.userCode WHERE 1 = 1
AND d.dicTypeCode = #{dicTypeCode}
AND d.dicValue LIKE concat('%', #{dicValue}, '%')
AND d.delFlag = #{delFlag}
ORDER BY d.updateTime DESC
