mapper.select:
SELECT u.userCode, u.userName, u.loginName, u.email, u.telephoneNumber, d1.dicValue AS role, d2.dicValue AS onlineStatus, d3.dicValue AS accountStatus, DATE_FORMAT( u.entryTime, '%Y-%m-%d' ) AS entryTime FROM user_info u LEFT JOIN dictionary_info d1 ON d1.dicTypeCode = 0 AND u.roleCode = d1.dicCode LEFT JOIN dictionary_info d2 ON d2.dicTypeCode = 1 AND u.onlineStatusCode = d2.dicCode LEFT JOIN dictionary_info d3 ON d3.dicTypeCode = 6 AND u.accountStatusCode = d3.dicCode WHERE (u.roleCode = 1 OR u.roleCode = 2)
AND u.userName LIKE concat('%',#{userName},'%')
AND u.roleCode = #{roleCode}
AND u.onlineStatusCode = #{onlineStatusCode}
AND u.accountStatusCode = #{accountStatusCode}
AND u.entryTime = #{entryTime}
ORDER BY u.updateTime DESC
